    private void RestaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_RestaMouseClicked
        // TODO add your handling code here:
       try{
       int suma = Integer.parseInt(N1.getText()) - Integer.parseInt(N2.getText());
        String M = " Resultado = ";
        Resultado.setText( M + Integer.toString(suma));
        N1.setText("");
        N2.setText("");
        
       }catch(NumberFormatException | java.util.InputMismatchException e){
       }
        
    }//GEN-LAST:event_RestaMouseClicked

    private void SumaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_SumaMouseClicked
        // TODO add your handling code here:
         try{
       int suma = Integer.parseInt(N1.getText()) + Integer.parseInt(N2.getText());
        String M = " Resultado = ";
        Resultado.setText( M + Integer.toString(suma));
        N1.setText("");
        N2.setText("");
        
       }catch(NumberFormatException | java.util.InputMismatchException e){
       }
    }//GEN-LAST:event_SumaMouseClicked

    private void MultMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MultMouseClicked
        // TODO add your handling code here:
         try{
       int suma = Integer.parseInt(N1.getText()) * Integer.parseInt(N2.getText());
        String M = " Resultado = ";
        Resultado.setText( M + Integer.toString(suma));
        N1.setText("");
        N2.setText("");
        
       }catch(NumberFormatException | java.util.InputMismatchException e){
       }
    }//GEN-LAST:event_MultMouseClicked

    private void DivicionMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_DivicionMouseClicked
        // TODO add your handling code here:
        try{
       int suma = Integer.parseInt(N1.getText()) / Integer.parseInt(N2.getText());
        String M = " Resultado = ";
        Resultado.setText( M + Integer.toString(suma));
        N1.setText("");
        N2.setText("");
        
       }catch(NumberFormatException | java.util.InputMismatchException | java.lang.ArithmeticException e){
       }
    }//GEN-LAST:event_DivicionMouseClicked


    
    
    
    
