    private void convertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertirActionPerformed
        // TODO add your handling code here:
        if (true == decimal.isSelected()) {
            b.setText(jd.D_Binario(Integer.parseInt(num.getText())));
            o.setText(String.valueOf(jd.D_Octal(Integer.parseInt(num.getText()))));
            h.setText(String.valueOf(jd.D_Hexadecimal(Integer.parseInt(num.getText()))));
        } else if (true == binario.isSelected()) {
            d.setText(String.valueOf(jd.Bn_B10(num.getText(), 2)));

            int nuevo = jd.Bn_B10(num.getText(), 2);
            o.setText(jd.D_Octal(nuevo));

            int nuev = jd.Bn_B10(num.getText(), 2);
            h.setText(jd.D_Hexadecimal(nuev));
        } else if (true == octal.isSelected()) {
            d.setText(String.valueOf(jd.Bn_B10(num.getText(), 8)));
            int ne = jd.Bn_B10(num.getText(), 8);
            b.setText(jd.D_Binario(ne));
            int net = jd.Bn_B10(num.getText(), 8);
            h.setText(jd.D_Hexadecimal(net));

        } else if (true == hexadecimal.isSelected()) {
            d.setText(String.valueOf(jd.Bn_B10(num.getText(), 16)));
            int ne1 = jd.Bn_B10(num.getText(), 16);
            b.setText(jd.D_Binario(ne1));
            int net1 = jd.Bn_B10(num.getText(), 16);
            o.setText(jd.D_Octal(net1));

        }

    }//GEN-LAST:event_convertirActionPerformed

